mapper.update:
UPDATE pms_sku_stock SET stock = CASE id
WHEN #{item.productSkuId} THEN stock - #{item.productQuantity}
END, lock_stock = CASE id
WHEN #{item.productSkuId} THEN lock_stock - #{item.productQuantity}
END WHERE id IN
#{item.productSkuId}

update oms_order
set status=#{status}
where id in

    #{id}

UPDATE pms_sku_stock SET lock_stock = CASE id
WHEN #{item.productSkuId} THEN lock_stock - #{item.productQuantity}
END WHERE id IN
#{item.productSkuId}

mapper.select:
SELECT o.id, o.status, o.total_amount, o.pay_amount, o.order_sn, o.member_id, ot.id ot_id, ot.product_id ot_product_id, ot.product_name ot_product_name, ot.product_pic ot_product_pic, ot.product_price ot_product_price, ot.product_sku_id ot_product_sku_id, ot.product_sku_code ot_product_sku_code, ot.product_quantity ot_product_quantity, ot.product_attr ot_product_attr FROM oms_order o LEFT JOIN oms_order_item ot ON o.id = ot.order_id WHERE o.delete_status = 0 and o.member_id=#{memberId}
and o.status=#{status}
ORDER BY o.create_time desc
