mapper.select:
SELECT sp.*, sfp.id r_id, sfp.flash_promotion_id r_flash_promotion_id, sfp.flash_promotion_session_id r_flash_promotion_session_id, sfp.product_id r_product_id, sfp.flash_promotion_price r_flash_promotion_price, sfp.flash_promotion_count r_flash_promotion_count, sfp.flash_promotion_limit r_flash_promotion_limit, sfp.sort r_sort FROM sms_flash_promotion sp LEFT JOIN sms_flash_promotion_product_relation sfp ON sfp.flash_promotion_id=sp.id WHERE NOW()>=sp.start_date AND sp.end_date>NOW() and status=1
AND sfp.product_id=#{pid}
ORDER BY sfp.sort DESC
